import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";

export default function DesktopHeader() {
  const { user } = useAuth();

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  return (
    <header className="bg-card border-b border-border px-6 py-4 flex items-center justify-between" data-testid="header-desktop">
      <div className="flex items-center space-x-4">
        <div className="gradient-bg text-white px-3 py-2 rounded-lg font-bold text-lg">
          RewardsPay
        </div>
      </div>
      <div className="flex items-center space-x-4">
        {/* Points Balance Desktop */}
        <div className="flex items-center space-x-2 bg-success text-success-foreground px-4 py-2 rounded-full points-animation">
          <span className="font-semibold" data-testid="text-points-desktop">
            {user?.points || 0} Points
          </span>
        </div>
        <Button variant="outline" onClick={handleLogout} data-testid="button-logout">
          <span>{user?.firstName || user?.email || 'User'}</span>
        </Button>
      </div>
    </header>
  );
}

client/src/components/desktop-sidebar.ts.content:
import { useLocation } from "wouter";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Home, FileText, Gift, User } from "lucide-react";

export default function DesktopSidebar() {
  const [location] = useLocation();

  const navItems = [
    { href: "/", icon: Home, label: "Dashboard" },
    { href: "/tasks", icon: FileText, label: "Tasks" },
    { href: "/rewards", icon: Gift, label: "Rewards" },
    { href: "/profile", icon: User, label: "Profile" },
  ];

  return (
    <aside className="w-64 bg-card border-r border-border" data-testid="sidebar-desktop">
      <nav className="p-4">
        <ul className="space-y-2">
          {navItems.map((item) => {
            const isActive = location === item.href;
            const Icon = item.icon;

            return (
              <li key={item.href}>
                <Link href={item.href}>
                  <Button
                    variant={isActive ? "default" : "ghost"}
                    className={`w-full justify-start ${
                      isActive 
                        ? "bg-primary text-primary-foreground" 
                        : "text-foreground hover:bg-secondary"
                    }`}
                    data-testid={`nav-${item.label.toLowerCase()}`}
                  >
                    <Icon className="mr-3 h-4 w-4" />
                    {item.label}
                  </Button>
                </Link>
              </li>
            );
          })}
        </ul>
      </nav>
    </aside>
  );
}

