client/src/components/task-card.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Star, FileText, Video, HandHeart } from "lucide-react";
import type { Task } from "@shared/schema";

interface ExternalSurvey {
  id: string;
  type: string;
  title: string;
  description: string;
  points: number;
  duration: string;
  category: string;
  rating: number;
  provider?: string;
  entryUrl?: string;
  isActive: boolean;
}

type TaskOrSurvey = Task | ExternalSurvey;

interface TaskCardProps {
  task: TaskOrSurvey;
  onComplete: (taskId: string, points: number) => void;
  isMobile?: boolean;
  isCompleting?: boolean;
}

export default function TaskCard({ task, onComplete, isMobile = false, isCompleting = false }: TaskCardProps) {
  const isExternalSurvey = 'provider' in task && task.provider;

  const handleComplete = () => {
    if (isExternalSurvey && task.entryUrl) {
      // Open external survey in new tab
      window.open(task.entryUrl, '_blank');
      // Award points after a short delay (in real app, this would be handled by webhooks)
      setTimeout(() => {
        onComplete(task.id, task.points);
      }, 1000);
    } else {
      onComplete(task.id, task.points);
    }
  };
  const getTaskIcon = () => {
    switch (task.type) {
      case "survey":
        return FileText;
      case "ad":
        return Video;
      case "offer":
        return HandHeart;
      default:
        return FileText;
    }
  };

  const getTaskColor = () => {
    switch (task.type) {
      case "survey":
        return "bg-primary/10 text-primary";
      case "ad":
        return "bg-red-500/10 text-red-500";
      case "offer":
        return "bg-success/10 text-success";
      default:
        return "bg-primary/10 text-primary";
    }
  };

  const getButtonColor = () => {
    switch (task.type) {
      case "survey":
        return "bg-primary hover:bg-primary/90";
      case "ad":
        return "bg-red-500 hover:bg-red-500/90";
      case "offer":
        return "bg-success hover:bg-success/90";
      default:
        return "bg-primary hover:bg-primary/90";
    }
  };

  const Icon = getTaskIcon();
  const rating = task.rating ? task.rating / 10 : 0;
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;

  const renderStars = () => {
    const stars = [];
    for (let i = 0; i < 5; i++) {
      if (i < fullStars) {
        stars.push(<Star key={i} className="h-3 w-3 fill-yellow-400 text-yellow-400" />);
      } else if (i === fullStars && hasHalfStar) {
        stars.push(<Star key={i} className="h-3 w-3 fill-yellow-400/50 text-yellow-400" />);
      } else {
        stars.push(<Star key={i} className="h-3 w-3 text-gray-300" />);
      }
    }
    return stars;
  };

  if (isMobile) {
    return (
      <Card className="task-card hover:shadow-md transition-all" data-testid={`task-card-${task.id}`}>
        <CardContent className="p-4">
          <div className="flex items-start justify-between mb-3">
            <div className="flex-1">
              <div className="flex items-center gap-2 mb-2">
                <div className={`p-2 rounded ${getTaskColor()}`}>
                  <Icon className="h-4 w-4" />
                </div>
                <div className="flex-1">
                  <h3 className="font-medium text-foreground text-sm">{task.title}</h3>
                  {isExternalSurvey && (
                    <p className="text-xs text-muted-foreground">{task.provider}</p>
                  )}
                </div>
              </div>
              <p className="text-muted-foreground text-xs mb-2">
                {task.duration && `${task.duration} • `}{task.category}
              </p>
              {task.rating > 0 && (
                <div className="flex items-center gap-1">
                  <div className="flex">{renderStars()}</div>
                  <span className="text-muted-foreground text-xs ml-1">{rating.toFixed(1)}</span>
                </div>
              )}
            </div>
            <Button
              className={`${getButtonColor()} text-white text-xs px-3 py-1 h-auto`}
              onClick={handleComplete}
              disabled={isCompleting}
              data-testid={`button-complete-${task.id}`}
            >
              {isCompleting ? "..." : isExternalSurvey ? "Start" : `${task.points} pts`}
            </Button>
          </div>
          {task.description && (
            <p className="text-muted-foreground text-xs">{task.description}</p>
          )}
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="task-card hover:shadow-md transition-all" data-testid={`task-card-${task.id}`}>
      <CardContent className="p-4">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center gap-2 mb-1">
              <h3 className="font-medium text-foreground">{task.title}</h3>
              {isExternalSurvey && (
                <Badge variant="secondary" className="text-xs">{task.provider}</Badge>
              )}
            </div>
            <p className="text-muted-foreground text-sm mt-1">
              {task.duration && `${task.duration} • `}{task.category}
            </p>
            {task.rating > 0 && (
              <div className="flex items-center mt-2">
                <div className="flex">{renderStars()}</div>
                <span className="text-muted-foreground text-xs ml-2">{rating.toFixed(1)}</span>
              </div>
            )}
            {task.description && (
              <p className="text-muted-foreground text-sm mt-2">{task.description}</p>
            )}
          </div>
          <Button
            className={`${getButtonColor()} text-white ml-4`}
            onClick={handleComplete}
            disabled={isCompleting}
            data-testid={`button-complete-${task.id}`}
          >
            {isCompleting ? "Completing..." : isExternalSurvey ? `Start Survey (${task.points} pts)` : `${task.points} pts`}
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/hooks/useAuth.ts.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { supabase } from "@/lib/supabase";
import type { User } from "@/lib/supabase";

export function useAuth() {
  const queryClient = useQueryClient();

  const { data: session, isLoading: sessionLoading } = useQuery({
    queryKey: ["session"],
    queryFn: async () => {
      const { data: { session } } = await supabase.auth.getSession();
      return session;
    },
    retry: false,
  });

  const { data: user, isLoading: userLoading } = useQuery({
    queryKey: ["user", session?.user?.id],
    queryFn: async () => {
      if (!session?.user?.id) return null;

      const { data, error } = await supabase
        .from("users")
        .select("*")
        .eq("id", session.user.id)
        .single();

      if (error && error.code !== "PGRST116") {
        throw error;
      }

      return data as User | null;
    },
    enabled: !!session?.user?.id,
    retry: false,
  });

  const signInMutation = useMutation({
    mutationFn: async ({ email, password }: { email: string; password: string }) => {
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });
      if (error) throw error;
      return data;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["session"] });
    },
  });

  const signUpMutation = useMutation({
    mutationFn: async ({ email, password }: { email: string; password: string }) => {
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
      });
      if (error) throw error;
      return data;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["session"] });
    },
  });

  const signOutMutation = useMutation({
    mutationFn: async () => {
      const { error } = await supabase.auth.signOut();
      if (error) throw error;
    },
    onSuccess: () => {
      queryClient.clear();
    },
  });

  return {
    user,
    session,
    isLoading: sessionLoading || userLoading,
    isAuthenticated: !!session?.user && !!user,
    signIn: signInMutation.mutate,
    signUp: signUpMutation.mutate,
    signOut: signOutMutation.mutate,
    isSigningIn: signInMutation.isPending,
    isSigningUp: signUpMutation.isPending,
    isSigningOut: signOutMutation.isPending,
  };
}

